/*自动播放1*/
var AUTOPLAY = 1;
/*连播1*/
var CONTIPLAY = 1;
/*处理自动播放选项和cookie*/
(function() {
var Tool = CommonTool;
var chk = Tool.byId('J_Video_Autoplay');
var ua = navigator.userAgent.toLowerCase();
var isIOS = /\((iPhone|iPad|iPod)/i.test(ua);
if (isIOS) {
console.log(chk.parentNode.style.display);
chk.parentNode.style.display = 'none';
return;
}
chk.parentNode.style.display = '';
var clickCookie = function() {
Tool.bindEvent(chk, 'change',
function() {
var chked = chk.checked;
Tool.writeCookie('ArtiVAuto', (chked ? 1 : 0), 24 * 365 * 10, '/', '.sina.com.cn');
});
}
var byCookie = function() {
var coo = Tool.readCookie('ArtiVAuto');
if (coo) {
if (parseInt(coo) == 0) {
chk.checked = false;
AUTOPLAY = 0;
}
}
};
clickCookie();
byCookie();
})();
/*获取第一个视频vid*/
var firstItem = CommonTool.byClass('J_Play_Item', 'J_Play_List')[0];
var fInfo = firstItem.getAttribute('play-data').split('-');
var fVid = fInfo[0];
var fPid = fInfo[1];
var sinaBokePlayerConfig_o = {
container: "J_Article_Player", //Div容器的id
width: 525,
height: 430,
playerWidth: 525, //宽
playerHeight: 430, //高
autoLoad: 1, //自动加载
autoPlay: AUTOPLAY, //自动播放
as: 0, //广告
pid: fPid,
tjAD: 0, //显示擎天柱广告
tj: 0, //片尾推荐
continuePlayer: 1, //连续播放
casualPlay: 1, //任意拖动视频
head: 0, //播放片头动画
logo: 0, //显示logo
share: 0,
thumbUrl: ""
};
(function() {
var toggle = function(id, hide) {
var e = CommonTool.byId(id);
var par = e.parentNode;
if (hide) {
CommonTool.addClass(par, e.className + '_disabled');
} else {
CommonTool.removeClass(par, e.className + '_disabled');
}
}
var scroll = new ScrollPic();
scroll.scrollContId = "J_Play_List"; //内容容器ID
scroll.arrLeftId = "J_Player_Prev"; //左箭头ID
scroll.arrRightId = "J_Player_Next"; //右箭头ID
scroll.listEvent = "onclick"; //切换事件
scroll.frameWidth = 532; //显示框宽度 **显示框宽度必须是翻页宽度的倍数
scroll.pageWidth = 133 * 3; //翻页宽度
scroll.upright = false; //垂直滚动
scroll.speed = 10; //移动速度(单位毫秒，越小越快)
scroll.space = 15; //每次移动像素(单位px，越大越快)
scroll.autoPlay = false; //自动播放
scroll.autoPlayTime = 5; //自动播放间隔时间(秒)
scroll.circularly = false;
scroll._move = scroll.move;
scroll.move = function(num, quick) {
scroll._move(num, quick);
toggle(scroll.arrRightId, scroll.eof);
toggle(scroll.arrLeftId, scroll.bof);
};
scroll.initialize(); //初始化
toggle(scroll.arrLeftId, scroll.bof);
})();
var autoPlayCheck = $("#J_Video_Autoplay")[0].checked;
var autoPlayFlv = 0;
var autoPlayH5 = false;
if(autoPlayCheck){
autoPlayFlv = 1;
var autoPlayH5 = true;
}
var flashConfig = {
url: "",	// flash播放器地址,
container : "J_Article_Player",
id: "myMovie",
width: 525,
height: 430,
params : {
allowNetworking : "all",
allowScriptAccess : "always",
wmode : "opaque",
allowFullScreen : "true",
quality : "high"
},
attributes: {},
flashvars: {
autoPlay: autoPlayFlv,  //是否自动播放
loop: 0,		
autoLoad: 1,
thumbUrl: '',
tj: 0,
as: 0
},
h5attr: {
autoPlay: autoPlayH5,  //是否自动播放
controls: true, //是否显示控制条
loop: false,		
poster: '', //视频加载前欲加载的图片地址，即播放器一开始显示的截图
preload: 'auto'
}
};
var videoList = [
{
video_id: 352998753,
pid:1,
url: 'https://video.sina.com.cn/p/finance/2020-09-17/detail-iivhvpwy7266800.d.html',
title:'纵横股份'//,
//swfOutsideUrl:"http://you.video.sina.com.cn/api/sinawebApi/outplayrefer.php/video_id=352998753/s.swf"
},
]
(function($){
var Play = {
init: function(flashConfig, videoList){
this.flashConfig = flashConfig;
this.videoList = videoList;
this.playVideo = playVideo;
this.prev = this.current = 0;
this.length = this.videoList.length;
this.contNode = $("#J_Video_Autoplay");
this.titleNode = $("#J_Video_Source");
this.playListNode = $("#J_Play_List .J_Play_Item");
if(videoList.length!==1){
//显示小图
CommonTool.byId('J_Play_List_Wrap').style.display='';
}
if(videoList.length= this.length - 1){
return;
}
this.current++;
this.play(this.prev, this.current);
},
play: function(prev, current){
this.player.playVideo(this.videoList[current]);
this.titleNode.html(this.videoList[current].title);
this.playListNode.eq(prev).removeClass("selected");
this.playListNode.eq(current).addClass("selected");
}
}
Play.init(flashConfig, videoList);
})(jQuery);
大疆创新迟迟未登陆资本市场，另一家无人机公司纵横股份反抢先一步递交了招股说明书，拟登陆A股科创板。
资料显示，纵横股份专注于工业无人机相关产品的研发、生产、销售及服务，是国内垂直起降固定翼无人机领域规模领先、最具市场竞争力的工业无人机企业之一。目前公司拥有大鹏CW-007、CW-10、CW-15等七大系列垂直起降固定翼无人机系统，广泛用于测绘与地理信息、巡检、安防监控等领域。
2017年-2019年，公司营业收入分别为1.03亿元、1.17亿元、2.11亿元，无人机系统为占比最大的核心业务。
虽同属无人机行业，但纵横与独角兽大疆的市场占有率相差甚远，自身竞争力不够强劲。根据Frost&Sullivan的报告，2019年大疆创新以55.1%的市场份额位于中国工业无人机整机市场第一，纵横股份则以5.4%的市场份额位列第二。纵横坦言，公司全国拓展的模式和手段单一，靠自身积累难以实现多领域的深度布局，在业务竞争中处于相对弱势地位。
另外，纵横还深陷专利诉讼的泥潭。据披露，2020年6月17日和18日，公司收到诉讼资料，河北雄安远度科技有限公司起诉纵横产品大鹏无人机及纵横股份未经许可，制造、销售、许诺销售侵害原告涉案专利权的产品，请求法院判令大鹏无人机及纵横股份立即停止侵权行为，并销毁库存被诉侵权产品及专用模具，支付涉案专利临时保护期使用费及维权合理开支合计2000万元。
若公司败诉，可能涉诉产品未来无法继续销售。
近年来，纵横股份的产品毛利率也明显滑坡。2017年-2019年，公司毛利率分别为70.98%、63.97%、56.51%，三年下降超过14%。对此，公司解释称主要系产品由无人机平台转变为加载各类载荷设备的无人机系统，产品服务结构的变化导致综合毛利率的下降。
同时，纵横股份在研发投入上略逊于同行，2019年研发费用率由去年的13.78%下降至8.88%，而A股同做无人机生意的华测导航、中海达、观典防务2019年的研发费用率分别为14.88%、13.68%、12.05%。
